def anagram1(S1,S2):    S1= (S1.replace(" ","")).lower()    S2= (S2.replace(" ","")).lower()    #str1=str1.replace("d","")    return sorted(S1)==sorted(S2)def anagram2(S1,S2):    S1=S1.replace(" ","").lower()    S2=S2.replace(" ","").lower()    count={}    if(len(S1)!=len(S2)):        print(False)    for each in S1:        if(each in count):            count[each]+=1        else:            count[each]=1    for each in S2:        if(each in count):            count[each]-=1        else:            count[each]=1    for each in count:        if(count[each]!=0):            return False        else:            return Trueprint(anagram1('deep','deed'))