# finding the single missing number in an arraydef find1(arr1,arr2):    if(len(arr1)!=len(arr2)):        num={}        for i in arr1:            num+=i        for i in arr2:            num-=i    return numdef finder(arr1,arr2):    # Sort the arrays    arr1.sort()    arr2.sort()    # Compare elements in the sorted arrays    for num1, num2 in zip(arr1,arr2):        if num1!= num2:            return num1    # Otherwise return last element    return arr1[-1]finder([1,2,3,4,5,6,7],[3,7,2,1,4,6])