#Given an integer array output unique pairs which output to value kdef pair_sum(arr,k):    arr=list(sorted(arr))    print(arr)    M=[]    if(len(arr)<2):        return    from itertools import combinations    for comb in combinations(arr, 2):        M.append(comb)        if(int(comb[0])+int(comb[1])==k):                print("ok",comb[0],comb[1])    # for i in M:    #         #print(i)    #         if(int(i[0])+int(i[1])==k):    #             print("ok",i[0],i[1])pair_sum([1,3,2,2],4)