class Solution(object):    def hIndex(self, citations):        """        :type citations: List[int]        :rtype: int        """        list1=[]        citations.sort(reverse=True)        #print citations        if(all(i==0 for i in citations)== True):            return 0        else:            for i in range(len(citations)):                if(len(citations)==1):                    if(citations[i]==0):                        return 0                    else:                        return 1                elif(citations[i]>=i+1):                    return i+1