#Reverse a given string and remove all trailing and begining spacesimport string## def sentence_Rev(string1):#     splits=[]#     #string1=string1.translate( { ord(c) :" " for c in string.whitespace } ).strip()#     string1=(' '.join(string1.split()))#     splits.extend(string1.split(" "))#     leng=len(splits)#     rev_string=[]#     for i in range(len(splits)) :#         rev_string.append(splits[leng-i-1])#     print( " ".join(rev_string))#     #print(rev_string)## sentence_Rev("      Dee               Mangal          ")#WITHOUT USING STRIP ANS JOIN  WE WILL HAVE TO FIND START AND END OF EACH WORD AND MAKE A LIST OF ALL THE WORDS WITHOUT WHITESPACESdef rev_sen(s):    length=len(s)    print(length)    spaces=[' ']    words=[]    i=0    while i < length :       #print(i)       if( s[i] not in spaces):           word_start=i           while (i < length and s[i] not in spaces ):               i=i+1           words.append(s[word_start:i])       i=i+1    print(" ".join(reversed(words)))rev_sen("           deeeee  ma")